INSERT INTO `text` VALUES ('391000', '剑冢可以投注心血祭炼装备，对强力、高级的装备是很实用的。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391001', '静室修炼时，会遇到心魔，记得及时去除。', '0', 'confidante 通用台词');
