INSERT INTO `text` VALUES ('391003', '多个心法是能同时起作用的，应该将所有心法都修炼起来。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391004', '记得修炼武学，如果遇到不可战胜的强敌，确认武学修炼到位。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391005', '部分神奇的丹药，能够永久或持续的增加身体素质，看到就买吧。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391006', '结交好友，可以让你江湖路越走越宽，记得维护你的人脉。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391007', '当你与某些门派交好时，是可以向他们学艺的。', '0', 'confidante 通用台词');
