INSERT INTO `text` VALUES ('391100', '君问归期未有期，巴山夜雨涨秋池。何当共剪西窗烛，却话巴山夜雨时。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391101', '山桃红花满上头，蜀江春水拍山流。花红易衰似郎意，水流无限似侬愁。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391102', '宜言饮酒，与子偕老。琴瑟在御，莫不静好。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391103', '知子之顺之，杂佩以问之。知子之好之，杂佩以报之。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391104', '我欲与君相知，长命无绝衰。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391105', '君在阴兮影不见，君依光兮妾所愿。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391106', '菱叶萦波荷飐风，荷花深处小舟通。逢郎欲语低头笑，碧玉搔头落水中。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391107', '荷叶生时春恨生，荷叶枯时秋恨成。深知身在情长在，怅望江头江水声。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391108', '昨夜星辰昨夜风，画楼西畔桂堂东。身无彩凤双飞翼，心有灵犀一点通。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391109', '纤云弄巧，飞星传恨，银汉迢迢暗度。金风玉露一相逢，便胜却人间无数。', '0', 'confidante 通用台词');
INSERT INTO `text` VALUES ('391110', '红笺小字，说尽平生意。鸿雁在云鱼在水，惆怅此情难寄。', '0', 'confidante 通用台词');
