INSERT INTO `text` VALUES ('450024', '虽然收了保护费，但那是上交老大的，我也没落着好啊！', '0', 'npc 地痞 台词');
INSERT INTO `text` VALUES ('450025', '多少拿点是我的辛苦费，你看这片市场多平静，还不是我们镇着场子。', '0', 'npc 地痞 台词');
INSERT INTO `text` VALUES ('450026', '虽说登不上台面，但我们比那些税丁好多了，至少我们拿了钱还做事。', '0', 'npc 地痞 台词');
INSERT INTO `text` VALUES ('450027', '人，都是要吃饭的。', '0', 'npc 恶霸 台词');
INSERT INTO `text` VALUES ('450028', '谁砸我们饭碗，我们就让他无法吃饭。', '0', 'npc 恶霸 台词');
INSERT INTO `text` VALUES ('450029', '我下面有人，哼哼，上面也有人！', '0', 'npc 恶霸 台词');
INSERT INTO `text` VALUES ('450030', '敦彼行苇，牛羊勿践履。方苞方体，维叶泥泥。戚戚兄弟，莫远具尔。或肆之筵，或授之几。', '0', '唐离台词');
INSERT INTO `text` VALUES ('4000100', '此山是我开，此树是我载……后面词都忘记了，反正就是把钱交出来！', '0', '第一章 第一节 村口 前置剧情');
INSERT INTO `text` VALUES ('4000101', '小女与奶奶前往投奔亲友，身无余财，还请各位好汉放过我们……', '0', '0');
INSERT INTO `text` VALUES ('4000102', '没财？小姑娘长得蛮水灵。和我们大王拜个堂，哪还用寻亲，哈哈哈！', '0', '0');
INSERT INTO `text` VALUES ('4000103', '这位小姐不要担心，且看我教训这毛贼！', '0', '第一章 第一节 村口 前置剧情');
INSERT INTO `text` VALUES ('4000104', '没事了，山贼已经被我打跑了。', '0', '第一章 第一节 村口 后置剧情');
INSERT INTO `text` VALUES ('4000105', '可是，可是我奶奶她……呜呜呜……\n', '0', '0');
INSERT INTO `text` VALUES ('4000106', '萧灵儿的奶奶吃不住惊吓，竟然就这么去了……', '0', '0');
INSERT INTO `text` VALUES ('4000107', '这帮可恶的山贼！小姐请在这稍等，我回村请村长来……看怎么处理后事。\n', '0', '0');
INSERT INTO `text` VALUES ('4000108', '谢谢这位少侠，呜呜呜……\n', '0', '0');
INSERT INTO `text` VALUES ('4000109', '你赶忙回村搬长辈来帮忙……\n', '0', '0');
INSERT INTO `text` VALUES ('4000110', '村长爷爷你可快点……\n', '0', '0');
INSERT INTO `text` VALUES ('4000111', '好好……可是人呢？\n', '0', '0');
INSERT INTO `text` VALUES ('4000112', '你领着村长过来后，除了老人的遗体，并无人影……\n', '0', '0');
INSERT INTO `text` VALUES ('4000113', '啊！一定是该死的山贼去而复返，把小姐掳走了！\n', '0', '0');
INSERT INTO `text` VALUES ('4000114', '这里交于老头子来处理，你速速去救人！', '0', '第一章 第一节 村口 后置剧情');
INSERT INTO `text` VALUES ('4000200', '你开始了飞奔。追了一阵，你看到了山贼和那位小姐的身影。', '0', '第一章 第二节 小径 前置剧情');
INSERT INTO `text` VALUES ('4000201', '看到了！该死的山贼，这回定不再留情！', '0', '0');
INSERT INTO `text` VALUES ('4000202', '啊，毛头小子竟然追上来了！【惨了惨了，又要挨一顿打了！】', '0', '第一章 第二节 小径 前置剧情');
INSERT INTO `text` VALUES ('4000204', '啊啊！你惹了我们黑风寨，没有好下场！看我们老大怎么收拾你！', '0', '第一章 第二节 小径 后置剧情');
INSERT INTO `text` VALUES ('4000205', '待我事情处理完了，自会上门讨教。', '0', '0');
INSERT INTO `text` VALUES ('4000206', '谢谢少侠多次相救……', '0', '0');
INSERT INTO `text` VALUES ('4000207', '现在你安全了。我爷爷应该已经在安顿老人家的后事……我们回去吧！', '0', '0');
INSERT INTO `text` VALUES ('4000208', '小女子姓萧，不知这位少侠如何称呼？', '0', '0');
INSERT INTO `text` VALUES ('4000209', '我叫#name#。这帮黑风贼，原来从不敢靠近凤鸣村的。', '0', '0');
INSERT INTO `text` VALUES ('4000210', '也不知道他们为何如此丧心病狂，才让姑娘遭此痛……', '0', '0');
INSERT INTO `text` VALUES ('4000211', '奶奶身体本就不好，就是放心不下我一人，才带我跋涉访亲。不想……', '0', '0');
INSERT INTO `text` VALUES ('4000212', '你就先安心在村里暂住几日，等我把黑风寨铲了，再护你去寻亲！', '0', '0');
INSERT INTO `text` VALUES ('4000213', '#name#你真是个好人……', '0', '0');
INSERT INTO `text` VALUES ('4000214', '处理完萧灵奶奶的后事，人也安顿好后，你这就打算前去独挑黑风寨！', '0', '0');
INSERT INTO `text` VALUES ('4000215', '等等，去之前去寻把称手的兵刃吧。', '0', '第一章 第二节 小径 后置剧情');
INSERT INTO `text` VALUES ('4000300', '山高林深，这黑风寨究竟是藏在什么地方？', '0', '第一章 第三节 密林 前置剧情');
INSERT INTO `text` VALUES ('4000301', '怎！怎么又是你！', '0', '0');
INSERT INTO `text` VALUES ('4000302', '正好我找不到你那破寨子，还不快带路！', '0', '0');
INSERT INTO `text` VALUES ('4000303', '你看我像那种出卖山寨的人吗？！', '0', '第一章 第三节 密林 前置剧情');
INSERT INTO `text` VALUES ('4000304', '别打啦！别打啦！我带路，我就是那种出卖山寨的人！', '0', '第一章 第三节 密林 后置剧情');
INSERT INTO `text` VALUES ('4000305', '早带路不就完事了？', '0', '0');
INSERT INTO `text` VALUES ('4000306', '我们寨里人多势众，各个都是杀人如麻的高手，你肯定打不赢！', '0', '0');
INSERT INTO `text` VALUES ('4000307', '我天赋异禀，只管玩乐，武学、心法会自行增长。你们这样的破寨子，我能挑十个！【不过还是赶紧提升下心法……】', '0', '第一章 第三节 密林 后置剧情');
INSERT INTO `text` VALUES ('4000400', '嘿嘿，小子，前面就是我们寨子的第一个岗哨了。', '0', '第一章 第四节 暗哨 前置剧情');
INSERT INTO `text` VALUES ('4000401', '看你挺年轻的，磕个头，爷爷就放了你。不然寨里兄弟可不像我那么好讲话！', '0', '0');
INSERT INTO `text` VALUES ('4000402', '你这是皮又痒了啊？', '0', '0');
INSERT INTO `text` VALUES ('4000403', '兄弟快来帮忙，这里有个不开眼的来找事啦！', '0', '第一章 第四节 暗哨 前置剧情');
INSERT INTO `text` VALUES ('4000404', '什么？！有人敢上黑风寨找事，这下可有乐子了！', '0', '第一章 第四节 暗哨 后置剧情');
INSERT INTO `text` VALUES ('4000405', '你出卖寨子，竟然将敌人带上门来！', '0', '0');
INSERT INTO `text` VALUES ('4000406', '【斜眼看了一下山贼2号】', '0', '0');
INSERT INTO `text` VALUES ('4000407', '我也带路，我也带路……【刚讨一顿好打的山贼顿时不敢再说什么】', '0', '0');
INSERT INTO `text` VALUES ('4000408', '【再往上走，估计敌人越来越强，就算临时抱佛脚也要去寻个武学来撑场面了】', '0', '第一章 第四节 暗哨 后置剧情');
INSERT INTO `text` VALUES ('4000500', '嘿嘿，小子，前面就是我们寨子的第二个岗哨了。', '0', '第一章 第五节 岗哨 前置剧情');
INSERT INTO `text` VALUES ('4000501', '怎么？然后叫我磕个头，就放了我？', '0', '0');
INSERT INTO `text` VALUES ('4000502', '对！哎？你怎么知道我要说什么？', '0', '0');
INSERT INTO `text` VALUES ('4000503', '你们山贼皮都很痒啊？', '0', '0');
INSERT INTO `text` VALUES ('4000504', '兄弟快来帮忙，这里有个不开眼的来找事啦！', '0', '第一章 第五节 岗哨 前置剧情');
INSERT INTO `text` VALUES ('4000505', '……前面就是黑风寨的山门了，兄弟再往上闯，这梁子可就结下了！', '0', '第一章 第五节 岗哨 后置剧情');
INSERT INTO `text` VALUES ('4000506', '你们竟然在凤鸣村村口打劫，我今天把你们的寨门打烂了，也占着理！', '0', '第一章 第五节 岗哨 后置剧情');
INSERT INTO `text` VALUES ('4000600', '凤鸣村竟然纵容毛头小子来捋我等虎须？！', '0', '第一章 第六节 山门 前置剧情');
INSERT INTO `text` VALUES ('4000601', '小的们，不用顾忌江湖道义，我们今天就将这小子打出屎来！', '0', '0');
INSERT INTO `text` VALUES ('4000602', '放他们两个单挑，我看谁敢仗着人多欺负凤鸣村的人！', '0', '0');
INSERT INTO `text` VALUES ('4000603', '凤鸣村的长辈们都过来助阵了，就连萧灵也来了……她用关切的眼神看着你。', '0', '第一章 第六节 山门 前置剧情');
INSERT INTO `text` VALUES ('4000604', '看起来#name#的心法、武学都大有长进啊……', '0', '第一章 第六节 山门 后置剧情');
INSERT INTO `text` VALUES ('4000605', '凤二叔什么时候回来的？', '0', '0');
INSERT INTO `text` VALUES ('4000606', '刚回，听到你上山了，就来给你压阵。', '0', '0');
INSERT INTO `text` VALUES ('4000607', '谢谢二叔！', '0', '第一章 第六节 山门 后置剧情');
INSERT INTO `text` VALUES ('4000700', '诸位，打搅凤鸣村并非黑风寨的本意……', '0', '第一章 第七节 二重山门 前置剧情');
INSERT INTO `text` VALUES ('4000701', '之前我在的时候，就与你们约定过井水不犯河水。', '0', '0');
INSERT INTO `text` VALUES ('4000702', '我才一阵子没回，就出了人命，之前约定自然作废。', '0', '0');
INSERT INTO `text` VALUES ('4000703', '至于之后如何，让苦主和我们的小拳头说话【看了你一眼】。', '0', '0');
INSERT INTO `text` VALUES ('4000704', '废话少说，看我砂钵大的拳头！', '0', '第一章 第七节 二重山门 前置剧情');
INSERT INTO `text` VALUES ('4000705', '累了吧？【拿出手绢，给你拭去额头的汗水】', '0', '第一章 第七节 二重山门 后置剧情');
INSERT INTO `text` VALUES ('4000706', '不累！看我打穿这个破山寨，为你奶奶报仇！', '0', '0');
INSERT INTO `text` VALUES ('4000707', '一直未婚的凤二叔斜眼盯着你……', '0', '第一章 第七节 二重山门 后置剧情');
INSERT INTO `text` VALUES ('4000800', '呼、呼……你应该是最后一个了吧……', '0', '第一章 第八节 三重山门 前置剧情');
INSERT INTO `text` VALUES ('4000801', '这场比武赢了我等道歉，输了也赔礼，但不打不服众！', '0', '0');
INSERT INTO `text` VALUES ('4000802', '我的大刀名断魂，三尺三，重三斤三。在之前红巾军给人打下手的时候，偷师了一招。勤学苦练后，我叫它断魂刀法，请赐教！', '0', '第一章 第八节 三重山门 前置剧情');
INSERT INTO `text` VALUES ('4000901', '黑风寨毁诺在先，我等也没什么好说的……但你们莫以为击败了我兄弟就万事大吉了！', '0', '第一章 第八节 三重山门 后置剧情');
INSERT INTO `text` VALUES ('4000902', '我等也是迫不得已，请进聚义厅一看便知！', '0', '第一章 第八节 三重山门 后置剧情');
INSERT INTO `text` VALUES ('4001000', '是何人喧哗？你们黑风寨果然就是个土匪窝，一点规矩都不懂！', '0', '第二章 第一节 广场鏖战1 前置剧情');
INSERT INTO `text` VALUES ('4001001', '就是你们在这吵吵囔囔？吵到少主怎么办？看我不教训教训你们！', '0', '0');
INSERT INTO `text` VALUES ('4001002', '看来黑风寨是换了个主人，难怪敢下山打扰凤鸣村。', '0', '0');
INSERT INTO `text` VALUES ('4001003', '反正一路打过去就是了！', '0', '第二章 第一节 广场鏖战1 前置剧情');
INSERT INTO `text` VALUES ('4001100', '兄弟们，黑风寨又找到撑腰的了，快来解决他们！', '0', '第二章 第二节 广场鏖战2 前置剧情');
INSERT INTO `text` VALUES ('4001101', '……', '0', '0');
INSERT INTO `text` VALUES ('4001102', '……', '0', '0');
INSERT INTO `text` VALUES ('4001103', '黑风寨的诸位，你们行不行啊……到底之前找了几个撑腰的……【捂脸】', '0', '0');
INSERT INTO `text` VALUES ('4001104', '他们仗着兵器之利，又经过战阵训练，自然是比我等做山贼的强几分……我们输得不服气，就找了几次帮手……【大头领讪讪的解释道】', '0', '第二章 第二节 广场鏖战2 前置剧情');
INSERT INTO `text` VALUES ('4001200', '你们休要得意，我们铸剑山庄卧虎藏龙，换个高手来就能把你们打趴下！', '0', '第二章 第三节 广场鏖战3 前置剧情');
INSERT INTO `text` VALUES ('4001201', '问都不用问，就自报家门了，真省事……', '0', '0');
INSERT INTO `text` VALUES ('4001202', '铸剑山庄的为何强夺黑风寨，还纵容山贼在我们村子附近打劫？', '0', '0');
INSERT INTO `text` VALUES ('4001203', '我们铸剑山庄的事，哪里轮得到你们管？！兄弟们快来！', '0', '第二章 第三节 广场鏖战3 前置剧情');
INSERT INTO `text` VALUES ('4001300', '你们虽强，但也只能走到这里了！我们铸剑山庄想要的东西，还没有得不到的！给我败了吧！', '0', '第二章  第四节 聚义厅1 前置剧情');
INSERT INTO `text` VALUES ('4001301', '你刚才说什么？', '0', '第二章  第四节 聚义厅1 后置剧情');
