INSERT INTO `text` VALUES ('4001400', '你们是何人，竟敢落我铸剑山庄颜面？！', '0', '第二章  第五节 聚义厅2 前置剧情');
INSERT INTO `text` VALUES ('4001401', '总算看到个管事的了！', '0', '0');
INSERT INTO `text` VALUES ('4001402', '还敢无视于我，小的们，给我上！', '0', '0');
