INSERT INTO `text` VALUES ('4005353', '这样坑二叔没问题吗？', '0', null);
INSERT INTO `text` VALUES ('4005354', '没问题！从小到大，我与二叔互相不知道坑几次了。', '0', null);
INSERT INTO `text` VALUES ('4005355', '总算找到你了！这回不能让你跑了！', '0', null);
INSERT INTO `text` VALUES ('4005356', '刚才我就没跑吧……', '0', null);
INSERT INTO `text` VALUES ('4005370', '这湖边的酒楼风光正好！', '0', '第六章 第三节 酒楼 前置剧情');
